var http = require("http");

var fs = require("fs");

var cassandra = require("cassandra-driver");

var client = new cassandra.Client({
  contactPoints: ['127.0.0.1'],
  localDataCenter: 'datacenter1',
  keyspace: 'olympiad'
});
http.createServer(function (req, res) {
  var str = req.url.split("-");
  str[1] = decodeURIComponent(str[1]);
  console.log("request: " + str[0]);

  switch (str[0]) {
    case "/":
      fs.readFile("./src/katya_pm2/laba7/index.html", function (err, content) {
        if (err) {
          res.writeHead(500, {
            "Content-Type": "text/html; charset=utf-8"
          });
          res.end(err.message, "utf-8");
          console.log(err);
        } else {
          res.writeHead(200, {
            "Content-Type": "text/html; charset=utf-8"
          });
          res.end(content, "utf-8");
        }
      });
      break;

    case "/find":
      res.writeHead(200, {
        "Content-Type": "text/html; charset=utf-8"
      });
      client.execute("SELECT * FROM olympiad WHERE country = ?", [str[1]], function (err, rows) {
        if (err) {
          console.log(err);
        } else {
          console.log(rows);
          var obj_str = JSON.stringify(rows);
          res.end(obj_str);
        }
      });
      break;

    default:
      res.writeHead(404, {
